import { useContext } from "react";
import classes from "./Cart.module.css";
import Modal from "../UI/Modal";
import CardContext from "../../context/cart-context";
import CartItem from "./CartItem";

function Cart(props) {
  const cartCtx = useContext(CardContext);
  const [checkoutIsShown, setCheckoutIsShown] = useState(false);

  const totalAmount = `$${cartCtx.totalAmount.toFixed(2)}`;
  const hasItem = cartCtx.items.length > 0;

  function cartItemRemoveHandler(id) {
    cartCtx.removeItem(id);
  }
  function cartItemAddHandler(item) {
    cartCtx.addItem({ ...item, amount: 1 }); // .addItem is in the cart provider
  }

  function showCheckoutHandler() {
    setCartIsShown(false);
    setCheckoutIsShown(true);
  }

  function hideCheckoutHandler() {
    setCheckoutIsShown(false);
  }

  const cartItems = (
    <ul className={classes["cart-items"]}>
      {cartCtx.items.map((item) => (
        <CartItem
          key={item.id}
          name={item.name}
          amount={item.amount}
          price={item.price}
          onRemove={cartItemRemoveHandler.bind(null, item.id)}
          onAdd={cartItemAddHandler.bind(null, item)}
        />
      ))}
    </ul>
  );

  return (
    <Modal onCloseCart2={props.onCloseCart}>
      {cartItems}
      <div className={classes.total}>
        <span>Total Amount</span>
        <span>{totalAmount}</span>
      </div>
      <div className={classes.actions}>
        <button className={classes["button--alt"]} onClick={props.onCloseCart}>
          Close
        </button>
        {hasItem && (
          <button onClick={props.onShowCheckout} className={classes.button}>
            Order
          </button>
        )}
      </div>
    </Modal>
  );
}

export default Cart;
