import classes from "./AvailableMeals.module.css";
import Card from "../UI/Card.js";
import MealItem from "./MealItem/MealItem";
import { useCallback, useEffect, useState } from "react";

const AvailableMeals = () => {
  const [curMeals, setMeals] = useState([]);
  const [isLoading, setIsLoading] = useState(false);

  const fetchMealsHandler = useCallback(async () => {
    setIsLoading(true);

    const response = await fetch(
      "https://food-project-c03a0-default-rtdb.europe-west1.firebasedatabase.app/Meals.json"
    );
    const data = await response.json();
    const meals = Object.values(data).map((meal) => {
      return {
        name: meal.name,
        description: meal.description,
        price: meal.price,
      };
    });
    setMeals(meals);
    setIsLoading(false);
  }, []);

  useEffect(() => {
    fetchMealsHandler();
  }, [fetchMealsHandler]);

  const mealsList = curMeals.map((meal) => {
    return (
      <MealItem
        key={meal.id}
        id={meal.id}
        name={meal.name}
        description={meal.description}
        price={meal.price}
      />
    );
  });

  return (
    <section className={classes.meals}>
      <Card>{isLoading ? "Loading..." : <ul>{mealsList}</ul>}</Card>
    </section>
  );
};

export default AvailableMeals;
