import { useEffect, useState } from "react";
import Modal from "../UI/Modal";
import classes from "./Checkout.module.css";
import useInputGenerator from "../hooks/input-generator";

const Checkout = (props) => {
  const confirmHandler = (event) => {
    event.preventDefault();

    // Todo => FETCH TO BACKEND

    nameReset();
    streetReset();
  };

  const {
    enteredValue: nameValue,
    inputIsValid: nameIsValid,
    inputHasError: nameHasError,
    valueChangeHandler: nameChangeHandler,
    blurHandler: nameBlurHandler,
    reset: nameReset,
  } = useInputGenerator((nameValue) => nameValue.trim() !== "");

  const {
    enteredValue: streetValue,
    inputIsValid: streetIsValid,
    inputHasError: streetHasError,
    valueChangeHandler: streetChangeHandler,
    blurHandler: streetBlurHandler,
    reset: streetReset,
  } = useInputGenerator((streetValue) => streetValue.trim() !== "");

  const {
    enteredValue: postValue,
    inputIsValid: postIsValid,
    inputHasError: postHasError,
    valueChangeHandler: postChangeHandler,
    blurHandler: postBlurHandler,
    reset: postReset,
  } = useInputGenerator((postValue) => postValue.trim() !== "");

  const [formIsValid, setFormIsValid] = useState(false);
  useEffect(() => {
    if (nameIsValid && streetIsValid && postIsValid) setFormIsValid(false);
    // else setFormIsValid(true);
  }, [nameIsValid, streetIsValid, postIsValid]);

  return (
    <Modal>
      <form className={classes.control} onSubmit={confirmHandler}>
        <div className={!nameHasError ? classes.control : classes.invalid}>
          <label htmlFor="name">Your Name</label>
          <input
            type="text"
            id="name"
            value={nameValue}
            onChange={nameChangeHandler}
            onBlur={nameBlurHandler}
          />
        </div>
        <div className={!streetHasError ? classes.control : classes.invalid}>
          <label htmlFor="street">Street</label>
          <input
            type="text"
            id="street"
            value={streetValue}
            onChange={streetChangeHandler}
            onBlur={streetBlurHandler}
          />
        </div>
        <div className={!nameHasError ? classes.control : classes.invalid}>
          <label htmlFor="postal">Postal Code</label>
          <input
            type="text"
            id="postal"
            value={nameValue}
            onChange={nameChangeHandler}
            onBlur={nameBlurHandler}
          />
        </div>
        <div className={!nameHasError ? classes.control : classes.invalid}>
          <label htmlFor="city">City</label>
          <input
            type="text"
            id="city"
            value={nameValue}
            onChange={nameChangeHandler}
            onBlur={nameBlurHandler}
          />
        </div>
        <div className={classes.actions}>
          <button type="button" onClick={props.onCloseCheckout}>
            Cancel
          </button>
          <button disabled={!formIsValid} className={classes.submit}>
            Confirm
          </button>
        </div>
      </form>
    </Modal>
  );
};

export default Checkout;
