import React, { useState } from "react";

import Header from "./components/Layout/Header";
import Meals from "./components/Meals/Meals";
import Cart from "./components/Cart/Cart";

// context
import CartProvider from "./context/CartProvider";
import Checkout from "./components/Checkout/Checkout";

// cart position doesnt matter becouse we use portals on it
function App() {
  const [cartIsShown, setCartIsShown] = useState(false);
  const [checkoutIsShown, setCheckoutIsShown] = useState(false);
  const [userData, seUserData] = useState([]);

  function showCartHandler() {
    setCartIsShown(true);
  }

  function hideCartHandler() {
    setCartIsShown(false);
  }

  function showCheckoutHandler() {
    setCartIsShown(false);
    setCheckoutIsShown(true);
  }

  function hideCheckoutHandler() {
    setCheckoutIsShown(false);
  }

  function onConfirm(data) {
    seUserData(data);
  }

  return (
    <CartProvider>
      {cartIsShown && (
        <Cart
          onCloseCart={hideCartHandler}
          onShowCheckout={showCheckoutHandler}
          onConfirm={userData}
        />
      )}
      {checkoutIsShown && (
        <Checkout onConfirm={onConfirm} onCloseCheckout={hideCheckoutHandler} />
      )}
      <Header onShowCart={showCartHandler} />
      <main>
        <Meals />
      </main>
    </CartProvider>
  );
}

export default App;
